import { useEffect, useState } from 'react';
import {Vortex} from 'react-loader-spinner';
import { BsX } from "react-icons/bs";
import IndividualEmployee from '../IndividualEmployee';
import './index.css'


const AllEmployees = () => {
  const [employees, setEmployees] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [isPopupOpen, setIsPopupOpen] = useState(false);


  useEffect(() => {
    fetchEmployees();

  }, [])

  const openPopup = () => {
    setIsPopupOpen(true);
  };

  const closePopup = () => {
    setIsPopupOpen(false);
  };

  const popupForm = () => (
    <form className='form' onSubmit={handleAddEmployeeForm}>
      <div className="d-flex justify-content-end m-0">
        <button className="close-button" onClick={closePopup}>
          <BsX />
        </button>
      </div>

      <div className="d-flex">
        <div className="left-container">
          <p className="mb-1 mt-2 ">Employee Name*</p>
          <input type="text" className="input" />

          <p className="mb-1 mt-2">Email</p>
          <input type="text" className="input" />

          <p className="mb-1 mt-2">Dob</p>
          <input type="date" className="input" />

          <p className="mb-1 mt-2">Address</p>
          <input type="text" className="input" />

          <p className="mb-1 mt-2">Qualifications</p>
          <input type="text" className="input" />
        </div>
        <div>
          <p className="mb-1 mt-2">Joining Date</p>
          <input type="date" className="input" />
          <p className="mb-1 mt-2">Designation</p>
          <input type="text" className="input" />
          <p className="mb-1 mt-2">Salary</p>
          <input type="text" className="input" />
          <p className="mb-1 mt-2">Department</p>
          <input type="text" className="input" />
        </div>
      </div>

      <button type="submit" className="mt-3 submit-btn">
        Submit
      </button>
    </form>
  );

  const fetchEmployees = async () => {
    try {
      const response = await fetch('http://192.168.0.158:8000/get_all_employees');
      const data = await response.json();

      const CamelCaseData = await data.map(employee => ({
        lastName: employee.last_name,
        empId: employee.id,
        dob: employee.date_of_birth,
        qualifications: employee.qualifications,
        department: employee.department,
        salary: employee.salary,
        username: employee.username,
        firstName: employee.first_name,
        email: employee.email,
        address: employee.address,
        designation: employee.designation,
        joiningDate: employee.joining_date
}))


      setEmployees(CamelCaseData)
      setIsLoading(false);
    } catch (error) {
      console.log('Error fetching employees:', error);
      setIsLoading(true);
    }
  };

  return (
    <div className='d-flex flex-column  align-items-center mt-2 '>
      <h1 className='heading'>All Employees of Openskale</h1>
      <button className="add-employee-btn" onClick={openPopup}>
        Add Employee
      </button>
      {isLoading ? (
        <div className=' tailspin d-flex flex-column justify-content-center'>
          <Vortex
  visible={true}
  height="80"
  width="80"
  ariaLabel="vortex-loading"
  wrapperStyle={{}}
  wrapperClass="vortex-wrapper"
  colors={['#003480','#97D7F7','#003E84','#97D7F7','#003480','#00ADEE']}
/>

        </div>) : (
        <table>
          <thead>
          <tr>
            <th>EMPLOYEENAME</th>
            <th>EMAIL</th>
            <th>DOB</th>
            <th>ADDRESS</th>
            <th>QUALIFICATIONS</th>
            <th>JOININGDATE</th>
            <th>DESIGNATION</th>
            <th>SALARY</th>
            <th>DEPARTMENT</th>

          </tr>
          </thead>
          <tbody>
          {

            employees.map((employee => (

              <IndividualEmployee data={employee} key={employee.empId}/>

            )))
          }
</tbody>
        </table>
      )}
      <div>
        {isPopupOpen && (
          <div className="popup-overlay">
            <div className="popup-content">{popupForm()}</div>
          </div>
        )}
      </div>
    </div>

  );
};

export default AllEmployees;
